import React from 'react';
import './appDrawer.css';
import Drawer from '../../images/drawer.png';


class AppDrawer extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            active: false,
        }
        this.handleClick = this.handleClick.bind(this);
    }

    componentDidMount() {
        if(window.matchMedia("(max-width: 700px)")) {
            const options = document.getElementById('options');
            options.parentNode.removeChild(options);
        }
    }

    handleClick() {
        this.setState({
            active:!this.state.active,
        });

        const drawer = document.getElementById('drawer').style;

        if(this.state.active) {
            drawer.animation = 'rotateRight 1s cubic-bezier(0.25, 0.46, 0.45, 0.94)'; 
            drawer.animationFillMode = 'forwards';
        } else {
            drawer.animation = 'rotateLeft 1s cubic-bezier(0.25, 0.46, 0.45, 0.94)';
            drawer.animationFillMode = 'forwards';
        }
    }

    render() {
        return (
            <div id="app-drawer">
                <img src={Drawer} alt="App drawer icon" onClick={this.handleClick} id="drawer"/>
                <ul id="options">
                    <li>Home</li>
                    <li>Projects</li>
                    <li>Research</li>
                    <li>Skills</li>
                    <li>Education</li>
                    <li>Resume</li>
                </ul>
            </div>
        );
    }
}

export default AppDrawer;
